def cut():
    f = open('iron.txt', 'r')
    data_in = f.readlines()
    # print(data_in)
    iron_r = []
    iron_g = []
    iron_b = []
    for i in data_in:
        i = i.strip()
        i = i[1:-3]
        i = i.split(', ')

        iron_r.append(int(i[0]))
        iron_g.append(int(i[1]))
        iron_b.append(int(i[2]))
        # pdb.set_trace()
    print(iron_r)
    print(iron_g)
    print(iron_b)

